@keyframes flash-operator {
  from {
    background-color: #d8d8d8;
  }
  to {
    background-color: transparent;
  }
}
@keyframes flash-operator-occurrence {
  from {
    background-color: rgba(159, 168, 74, 0.5);
  }
  to {
    background-color: transparent;
  }
}
@keyframes flash-operator-remove-occurrence {
  from {
    background-color: rgba(204, 102, 102, 0.5);
  }
  to {
    background-color: transparent;
  }
}
@keyframes flash-undo-redo {
  from {
    background-color: #d8d8d8;
  }
  to {
    background-color: transparent;
  }
}
@keyframes flash-undo-redo-multiple-changes {
  from {
    background-color: rgba(159, 168, 74, 0.5);
  }
  to {
    background-color: transparent;
  }
}
@keyframes flash-undo-redo-multiple-deletes {
  from {
    background-color: rgba(204, 102, 102, 0.5);
  }
  to {
    background-color: transparent;
  }
}
@keyframes flash-search {
  from {
    background-color: rgba(16, 17, 19, 0.8);
  }
  to {
    background-color: transparent;
  }
}
@keyframes flash-screen {
  from {
    background-color: #d8d8d8;
  }
  to {
    background-color: transparent;
  }
}
atom-text-editor .vim-mode-plus-flash.operator .region {
  animation-name: flash-operator;
  animation-duration: 0.5s;
  animation-iteration-count: 1;
}
atom-text-editor .vim-mode-plus-flash.operator-long .region {
  animation-name: flash-operator;
  animation-duration: 0.8s;
  animation-iteration-count: 1;
}
atom-text-editor .vim-mode-plus-flash.operator-occurrence .region {
  animation-name: flash-operator-occurrence;
