}
atom-text-editor .vim-mode-plus-flash.operator-remove-occurrence .region {
  animation-name: flash-operator-remove-occurrence;
  animation-duration: 0.8s;
  animation-iteration-count: 1;
}
atom-text-editor .vim-mode-plus-flash.undo-redo .region {
  animation-name: flash-undo-redo;
  animation-duration: 0.5s;
  animation-iteration-count: 1;
}
atom-text-editor .vim-mode-plus-flash.undo-redo-multiple-changes .region {
  animation-name: flash-undo-redo-multiple-changes;
