  animation-iteration-count: 1;
}
atom-text-editor .vim-mode-plus-flash.search .region {
  animation-name: flash-search;
  animation-duration: 1s;
  animation-iteration-count: 1;
  z-index: 1;
}
atom-text-editor .vim-mode-plus-flash.screen .region {
  animation-name: flash-screen;
  animation-duration: 0.3s;
  animation-iteration-count: 1;
}
