.vim-mode-plus-hover {
  display: block;
  color: #dbdbdb;
  background-color: #333333;
  border-radius: 3px;
  box-shadow: 0 0 1px #1d1f21;
  margin-top: -3em;
  margin-left: -0.3em;
  padding-left: 0.2em;
  padding-right: 0.2em;
  text-align: center;
}
.vim-mode-plus-hover.first {
  background-color: #0f82e6;
}
.vim-mode-plus-hover.last {
  background-color: #d13b2e;
}
atom-text-editor[mini].vim-mode-plus-input {
  background-color: inherit;
  font-weight: normal;
  color: #a8a8a8;
  line-height: 1.28;
  cursor: default;
  white-space: nowrap;
  padding-left: 10px;
  height: 0px !important;
  width: 0px !important;
  overflow: hidden !important;
  border: none !important;
  padding: 0 !important;
  display: block !important;
  position: fixed !important;
  top: -10px !important;
  left: -10px !important;
}
.vim-mode-plus-search-container {
  display: flex;
}
.vim-mode-plus-search-container > div {
  padding: 5px 10px;
  border: none;
}
.vim-mode-plus-search-container .editor-container {
  width: 100%;
}
atom-text-editor[mini].vim-mode-plus-search {
  border: none;
  color: #a8a8a8;
  cursor: default;
}
atom-text-editor[mini].vim-mode-plus-search.is-focused {
  box-shadow: none;
}
atom-text-editor[mini].vim-mode-plus-search::before {
  font-size: 1.2em;
  padding-right: 0.5em;
  content: "/";
}
atom-text-editor[mini].vim-mode-plus-search.backwards::before {
  content: "?";
}
atom-text-editor:not(.silent) .vim-mode-plus-search-match .region {
  background-color: rgba(180, 183, 180, 0.4);
}
atom-text-editor:not(.silent) .vim-mode-plus-search-match.first .region {
  background-color: rgba(129, 162, 190, 0.4);
}
atom-text-editor:not(.silent) .vim-mode-plus-search-match.last .region {
  background-color: rgba(204, 102, 102, 0.4);
}
@keyframes flash-find-char {
  from {
    border-color: #81a2be;
  }
  to {
    border-color: transparent;
  }
}
.round-box {
  box-sizing: border-box;
  border-radius: 3px;
}
atom-text-editor .vim-mode-plus-search-match .region {
  box-sizing: border-box;
  border-radius: 3px;
  border: 2px solid transparent;
  transition: border-color 0.2s;
}
atom-text-editor .vim-mode-plus-search-match.current .region {
  border-color: #b4b7b4;
  transition-duration: 0.1s;
}
atom-text-editor .vim-mode-plus-search-match.first.current .region {
  border-color: search-match-border-first;
}
atom-text-editor .vim-mode-plus-search-match.last.current .region {
  border-color: syntax-color-removed;
}
atom-text-editor .vim-mode-plus-highlight-search .region {
  box-sizing: border-box;
  border-radius: 3px;
  border: 1px solid rgba(29, 31, 33, 0.3);
  background-color: #e0e0e0;
}
atom-text-editor .vim-mode-plus-persistent-selection .region {
  background-color: #e0e0e0;
}
atom-text-editor .vim-mode-plus-target-range .region {
  background-color: #e0e0e0;
}
atom-text-editor .vim-mode-plus-occurrence-base .region {
  box-sizing: border-box;
  border-bottom-width: 2px;
  border-bottom-style: dotted;
  z-index: 1;
  border-color: #de935f;
}
atom-text-editor .vim-mode-plus-find-char .region {
  box-sizing: border-box;
  border-width: 0px;
  border-bottom-width: 2px;
  border-style: solid;
}
atom-text-editor .vim-mode-plus-find-char.pre-confirm .region {
  border-color: #de935f;
}
atom-text-editor .vim-mode-plus-find-char.pre-confirm .region.current {
  border-bottom-width: 5px;
}
atom-text-editor .vim-mode-plus-find-char.post-confirm .region {
  animation-name: flash-find-char;
  animation-duration: 2s;
  animation-iteration-count: 1;
}
atom-text-editor .vim-mode-plus-find-char.post-confirm .region.long {
  animation-duration: 4.0s;
}
atom-workspace.vim-mode-plus--pane-maximized atom-dock.left {
  display: none;
}
atom-workspace.vim-mode-plus--pane-maximized atom-dock.right {
  display: none;
}
atom-workspace.vim-mode-plus--pane-maximized atom-dock.bottom {
  display: none;
}
atom-workspace.vim-mode-plus--pane-maximized atom-pane-container {
  position: relative;
}
atom-workspace.vim-mode-plus--pane-maximized atom-pane-container atom-pane-axis:not(.vim-mode-plus--active-pane-axis) {
  display: none;
}
atom-workspace.vim-mode-plus--pane-maximized atom-pane-container atom-pane {
  display: none;
}
atom-workspace.vim-mode-plus--pane-maximized atom-pane-container atom-pane .item-views {
  background: #ffffff !important;
}
atom-workspace.vim-mode-plus--pane-maximized atom-pane-container atom-pane.vim-mode-plus--active-pane {
  display: flex;
  position: absolute;
  top: 0;
  right: 0;
  left: 0;
  bottom: 0;
  z-index: 100;
}
atom-workspace.vim-mode-plus--pane-maximized.vim-mode-plus--hide-tab-bar .tab-bar {
  display: none;
}
atom-workspace.vim-mode-plus--pane-maximized.vim-mode-plus--hide-status-bar .status-bar {
  display: none;
}
atom-workspace.vim-mode-plus--pane-maximized.vim-mode-plus--pane-centered atom-text-editor:not(.mini) {
  margin-left: 20%;
}
atom-workspace.vim-mode-plus--pane-maximized.vim-mode-plus--pane-centered atom-text-editor:not(.mini) atom-text-editor {
  margin-left: 0%;
}
.vim-mode-plus-select-list .list-group .character-match {
  color: #dbdbdb;
  font-weight: bold;
}
.demo-mode-container .kind {
  color: white;
}
